	public List<String> buildNewMIScript(List<String> metaList, List<HashMap<String, String>> countryList,
			String[] includeTables) {

		try {
			List<String> newMetaList = new ArrayList<String>();
			/*
			 * 1st RULE: REMOVE Script lines in Include Tables
			 */
			for (String mi : metaList) {
				if (!mi.contains("INSERT") && !mi.contains("INTO")) {
					newMetaList.add(mi);
				}
 				for (String incTable : includeTables) {
					if (mi.contains(incTable)) {
						newMetaList.add(mi);
					}
				}
 			}

			/*
			 * 2nd RULE: REMOVE Script lines with old Country
			 */
			List<String> finalList = new ArrayList<>();
			for (String mi : newMetaList) {
				boolean newData = true;
				for (HashMap<String, String> countryMap : countryList) {
					String oldCountry = new StringBuilder("'")
							.append(StringUtils.trimToEmpty(countryMap.get(COUNTRY_CODE))).append("'").toString();
					if (mi.contains(oldCountry)) {
						newData = false;
						break;
					}
				}
				if (newData) {
					finalList.add(mi);
				}
			}

			return finalList;
		} catch (Exception e) {
			logger.error("** CountryMetaUtil : buildNewMIScript >> ERROR Parsing Script ", e);
		}

		return metaList;
	}

	public List<String> buildNewHiveScript(List<String> hiveList, List<HashMap<String, String>> countryList) {

		try {
			List<String> newHiveList = new ArrayList<String>();
			/*
			 * 1st RULE: Remove T11 HQL of create table.
			 */
			for (String hc : hiveList) {
				if (hc.contains(STAGING_TAG)) {
					newHiveList.add(hc);
				}
			}

			/*
			 * 2nd RULE: Remove T10 Scripts for Old Countries
			 */
			List<String> finalList = new ArrayList<>();
			for (String hc : newHiveList) {
				boolean newData = true;
				for (HashMap<String, String> countryMap : countryList) {
					String oldCountry = new StringBuilder("_")
							.append(StringUtils.trimToEmpty(countryMap.get(COUNTRY_CODE))).append("_").toString();
					if (hc.contains(oldCountry)) {
						newData = false;
						break;
					}
				}
				if (newData) {
					finalList.add(hc);
				}
			}
			return finalList;
		} catch (Exception e) {
			logger.error("** CountryMetaUtil: buildNewHiveScript >> ERROR Parsing Script ", e);
		}

		return hiveList;
	}

